mapper.select:
SELECT schedules_id, room_id, start_time, end_time, content
FROM schedules
WHERE room_id = #{roomId} AND schedule_id = #{scheduleId}

mapper.update:
UPDATE schedules
SET start_time = #{startTime},
    end_time = #{endTime},
    content = #{content}
WHERE schedules_id = #{scheduleId} AND room_id = #{roomId}
