mapper.insert:
INSERT INTO teacher_notices (
    room_id,
    content,
    title
) VALUES (
             #{roomId},
             #{content},
             #{title}
         )

mapper.select:
SELECT notice_id, room_id, content, created_at, title, status
FROM teacher_notices
WHERE notice_id = #{noticeId} AND room_id = #{roomId}

mapper.update:
UPDATE teacher_notices
SET status = #{status}
WHERE notice_id = #{noticeId}
